using FluentAssertions;
using LeetCodeCSharp.Medium;
using Xunit;

namespace SolutionsValidator.Medium
{
    public class LongestPalindromicSubstringValidator
    {
        [Theory]
        [InlineData("babad", "bab")]
        [InlineData("cbbd", "bb")]
        [InlineData("a", "a")]
        [InlineData("", "qahaq")]
        public void OnSuccess_Should_Find_Correct_Answer_For_LongestPalindromicSubstringValidator(string originalString, string expectedOutput)
        {
            //Arrange+Act
            var result = LongestPalindromicSubstring.SolveLongestPalindromicSubstring(originalString);

            //Assert
            result.Should().Be(expectedOutput);
        }
    }
}